repeat wait() until game:IsLoaded();local sWF=true;spawn(function()while wait(3) and sWF do pcall(function()if game:GetService("GuiService"):GetErrorCode().Value>=Enum.ConnectionError.DisconnectErrors.Value and game:GetService("GuiService"):GetErrorCode().Value<Enum.ConnectionError.PlacelaunchOtherError.Value then sWF=false end end) pcall(function()local serverUnix=workspace:GetServerTimeNow();writefile(game.Players.LocalPlayer.Name.."_checkyum.json",string.format('{"playerName":"%s","time":"%s"}',game.Players.LocalPlayer.Name,os.date("!%Y-%m-%dT%H:%M:%SZ",serverUnix))) end) end end)
